mapper.select:
SELECT * FROM bill


        AND id = #{id}


ORDER BY recordtime DESC

mapper.update:
UPDATE bill
SET name       = #{name}, type = #{type}, money = #{money},
    balance    = #{balance}, member = #{member},
    recordtime = #{recordtime}, comment = #{comment}
WHERE id = #{id}

mapper.select:
SELECT b.*,m.name AS memberName,t.name AS typeName FROM bill b LEFT JOIN familymember m ON b.member = m.id LEFT JOIN billtype t ON b.type = t.id
AND b.id = #{id}
ORDER BY recordtime DESC
