mapper.select:
select

from book_user_password


        AND id = #{id}


        AND account = #{account}


        AND password = password(#{password})


        AND old_password = password(#{oldPassword})


ORDER BY update_time DESC

    LIMIT #{start} ,#{offset}

mapper.insert:
INSERT INTO book_user_password (account, password, old_password,
                                creator, create_name, create_time,
                                updater, update_name, update_time
)
VALUES (#{account,jdbcType=VARCHAR}, password(#{password,jdbcType=VARCHAR}),
        password(#{oldPassword,jdbcType=VARCHAR}),
        #{creator,jdbcType=BIGINT}, #{createName,jdbcType=VARCHAR}, #{createTime,jdbcType=TIMESTAMP},
        #{updater,jdbcType=BIGINT}, #{updateName,jdbcType=VARCHAR}, #{updateTime,jdbcType=TIMESTAMP}
)
